{"title":"k8-cluster-visualization-zoomable","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "title": "Kubernetes overview",
  "autosize": {
    "type": "fit",
    "contains": "padding"
  },
  "padding": 20,
  "signals": [
    {
      "name": "duration",
      "init": "750",
      "description": "The duration for the zoom transitions. Fade-in transitions will be the same duration, but will be delayed per the amount set here.",
      "on": [
        {
          "events": {"type": "click", "marknames": ["circles", "background"]},
          "update": "(event.metaKey || event.ctrlKey ? 4 : 1) *750"
        }
      ]
    },
    {
      "name": "k",
      "value": 1,
      "description": "The scale used for zooming based on the focused node",
      "on": [
        {
          "events": [{"signal": "focus"}],
          "update": "focus ? width/(focus.r*2) : 1"
        }
      ]
    },
    {
      "name": "root",
      "update": "{'currentNode': data('tree')[0]['currentNode'], 'x': data('tree')[0]['x'], 'y': data('tree')[0]['y'], 'r': data('tree')[0]['r'], 'k': 1, 'children': data('tree')[0]['children']}",
      "description": "The root node in the hierarchy"
    },
    {
      "name": "focus",
      "init": "root",
      "description": "The zoomed-in node in the hierarchy",
      "on": [
        {
          "events": {"type": "click", "markname": "background"},
          "update": "{id: root['currentNode'], 'x': root['x'], 'y': root['y'], 'r': root['r'], 'k': 1,'children': root['children']}"
        },
        {
          "events": {"type": "click", "markname": "circles"},
          "update": "(focus['x'] === datum['x'] && focus['y'] === datum['y'] && focus['r'] === datum['r'] && focus['r'] !== root['r']) ? {'currentNode': root['currentNode'], 'x': root['x'], 'y': root['y'], 'r': root['r'], 'k': 1, 'children': root['children']} : {'currentNode': datum['currentNode'], 'x': datum['x'], 'y': datum['y'], 'r': datum['r'], 'k': k, 'children': datum['children']}"
        }
      ]
    },
    {
      "name": "focus0",
      "update": "data('focus0') && length(data('focus0'))>0 ? data('focus0')[0] : focus",
      "description": "The prior zoomed-in node in the hierarchy"
    },
    {
      "name": "timer",
      "description": "The timer to be used for transitions such as zoom, fade, etc.",
      "on": [{"events": "timer", "update": "now()"}]
    },
    {
      "name": "interpolateTime",
      "description": "the start and end times in miliseconds for animation interpolations",
      "on": [
        {
          "events": {
            "type": "click",
            "marknames": ["circles", "background"]
          },
          "update": "{'start': timer, 'end': timer+duration}"
        }
      ]
    },
    {
      "name": "t",
      "description": "The normalized time for easing",
      "update": "interpolateTime ? clamp((timer-interpolateTime.start)/(interpolateTime.end-interpolateTime.start), 0, 1): null"
    },
    {
      "name": "tEase",
      "description": "The easing calculation. Currently set as easeInOutCubic",
      "update": "t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1"
    },
    {
      "name": "interpolateTimeDelayed",
      "description": "The delayed time for easing",
      "on": [
        {
          "events": {"signal": "interpolateTime"},
          "update": "{'start': interpolateTime['end'], 'end': interpolateTime['end']+duration}"
        }
      ]
    },
    {
      "name": "tDelayed",
      "description": "The delayed normalized time for easing",
      "update": "interpolateTimeDelayed ? clamp((timer-interpolateTimeDelayed.start)/(interpolateTimeDelayed.end-interpolateTimeDelayed.start), 0, 1): null"
    },
    {
      "name": "tEaseDelayed",
      "description": "The delayed easing calculation. Currently set as easeInOutCubic",
      "update": "tDelayed < 0.5 ? 4 * tDelayed * tDelayed * tDelayed : (tDelayed - 1) * (2 * tDelayed - 2) * (2 * tDelayed - 2) + 1"
    },
    {
      "name": "showDetails",
      "description": "A boolean to indicate whether to show a node's details",
      "value": false,
      "on": [
        {
          "events": {
            "type": "click",
            "marknames": ["circles", "background"],
            "filter": [
              "!event.altKey && !event.shiftKey",
              "event.button === 0"
            ],
            "markname": "circles"
          },
          "update": "focus['children'] > 0 ? false : datum['currentNode'] === root['currentNode'] || focus0['currentNode'] !== root['currentNode'] && focus['currentNode'] === root['currentNode'] ? false : true"
        },
        {
          "events": {
            "type": "click",
            "marknames": ["circles", "background"],
            "filter": ["event.altKey || event.shiftKey", "event.button === 0"]
          },
          "update": "focus0['currentNode'] === focus['currentNode'] ? !showDetails : true"
        }
      ]
    },
    {
      "name": "rootNodeName",
      "description": "The name of the root node / the environment name",
      "value": "GKE"
    }
  ],
  "data": [
    {
      "name": "raw-data",
      "url": {
        "%context%": true,
        "index": "ss4o_logs-k8s-k8s-sample-sample",
        "body": {
          "size": 0,
          "aggs": {
            "container": {
              "terms": {
                "field": "kubernetes.container.id.keyword",
                "size": 3000
              },
              "aggs": {
                "top-hits": {
                  "top_hits": {
                    "sort": [
                      {
                        "@timestamp": {
                          "order": "desc"
                        }
                      }
                    ],
                    "_source": {
                      "includes": [
                        "kubernetes.container.name",
                        "kubernetes.pod.name",
                        "kubernetes.node.name",
                        "kubernetes.namespace"
                      ]
                    },
                    "size": 1
                  }
                }
              }
            }
          }
        }
      },
      "format": {
        "property": "aggregations.container.buckets"
      },
      "transform": [
        {
          "type": "formula",
          "as": "node",
          "expr": "datum['top-hits'].hits.hits == null ? 'NA' :  datum['top-hits'].hits.hits[0]._source.kubernetes.node.name"
        },
        {
          "type": "formula",
          "as": "pod",
          "expr": "datum['top-hits'].hits.hits == null ? 'NA' :  datum['top-hits'].hits.hits[0]._source.kubernetes.pod.name"
        },
        {
          "type": "formula",
          "as": "namespace",
          "expr": "datum['top-hits'].hits.hits == null ? 'NA' :  datum['top-hits'].hits.hits[0]._source.kubernetes.namespace"
        },
        {
          "type": "formula",
          "as": "container",
          "expr": "datum['top-hits'].hits.hits == null ? 'NA' :  datum['top-hits'].hits.hits[0]._source.kubernetes.container.name"
        },
        {
          "type": "formula",
          "as": "value",
          "expr": "1"
        },
        {
          "type": "project",
          "fields": [
            "key",
            "pod",
            "node",
            "namespace",
            "value",
            "container"
          ],
          "as": [
            "id",
            "pod",
            "node",
            "namespace",
            "value",
            "container"
          ]
        }
      ]
    },
    {
      "name": "isolatedLevel1",
      "source": [
        "raw-data"
      ],
      "transform": [
        {
          "type": "project",
          "fields": [
            "node",
            "currentNode",
            "value"
          ],
          "as": [
            "currentNode",
            "parent",
            "value"
          ]
        },
        {
          "type": "aggregate",
          "groupby": [
            "parent",
            "currentNode"
          ],
          "fields": [
            "value"
          ],
          "ops": [
            "sum"
          ],
          "as": [
            "value"
          ]
        },
        {
          "type": "formula",
          "as": "parent",
          "expr": "!isDefined(datum.parent) ? rootNodeName : datum.parent"
        },
        {
          "type": "project",
          "fields": [
            "currentNode",
            "parent",
            "value"
          ],
          "as": [
            "currentNode",
            "parent",
            "value"
          ]
        },
        {
          "type": "filter",
          "expr": "datum.currentNode"
        },
        {
          "type": "formula",
          "as": "type",
          "expr": "'node'"
        }
      ]
    },
    {
      "name": "rootNode",
      "source": "isolatedLevel1",
      "transform": [
        {
          "type": "project",
          "fields": [
            "parent",
            "pod"
          ],
          "as": [
            "currentNode",
            "parent"
          ]
        },
        {
          "type": "aggregate",
          "groupby": [
            "currentNode",
            "parent"
          ]
        },
        {
          "type": "project",
          "fields": [
            "currentNode",
            "parent"
          ],
          "as": [
            "currentNode",
            "parent"
          ]
        },
        {
          "type": "formula",
          "as": "type",
          "expr": "'environment'"
        }
      ]
    },
    {
      "name": "isolatedLevel2",
      "source": "raw-data",
      "transform": [
        {
          "type": "project",
          "fields": [
            "node",
            "pod",
            "value"
          ],
          "as": [
            "parent",
            "currentNode",
            "value"
          ]
        },
        {
          "type": "aggregate",
          "groupby": [
            "parent",
            "currentNode"
          ],
          "fields": [
            "value"
          ],
          "ops": [
            "sum"
          ],
          "as": [
            "value"
          ]
        },
        {
          "type": "project",
          "fields": [
            "currentNode",
            "parent",
            "value"
          ],
          "as": [
            "currentNode",
            "parent",
            "value"
          ]
        },
        {
          "type": "filter",
          "expr": "datum.currentNode"
        },
        {
          "type": "filter",
          "expr": "datum.parent"
        },
        {
          "type": "formula",
          "as": "type",
          "expr": "'pod'"
        }
      ]
    },
    {
      "name": "isolatedLevel3",
      "source": "raw-data",
      "transform": [
        {
          "type": "project",
          "fields": [
            "pod",
            "id",
            "value"
          ],
          "as": [
            "parent",
            "currentNode",
            "value"
          ]
        },
        {
          "type": "aggregate",
          "groupby": [
            "parent",
            "currentNode"
          ],
          "fields": [
            "value"
          ],
          "ops": [
            "sum"
          ],
          "as": [
            "value"
          ]
        },
        {
          "type": "project",
          "fields": [
            "currentNode",
            "parent",
            "value"
          ],
          "as": [
            "currentNode",
            "parent",
            "value"
          ]
        },
        {
          "type": "filter",
          "expr": "datum.currentNode"
        },
        {
          "type": "filter",
          "expr": "datum.parent"
        },
        {
          "type": "formula",
          "as": "type",
          "expr": "'container'"
        }
      ]
    },
    {
      "name": "tree",
      "source": [
        "rootNode",
        "isolatedLevel1",
        "isolatedLevel2",
        "isolatedLevel3"
      ],
      "transform": [
        {
          "type": "lookup",
          "from": "raw-data",
          "key": "id",
          "fields": [
            "currentNode"
          ],
          "values": [
            "namespace",
            "container"
          ],
          "as": [
            "namespace-container",
            "container"
          ],
          "default": ""
        },
        {
          "type": "lookup",
          "from": "raw-data",
          "key": "pod",
          "fields": [
            "currentNode"
          ],
          "values": [
            "namespace"
          ],
          "as": [
            "namespace-pod"
          ],
          "default": ""
        },
        {
          "type": "lookup",
          "from": "raw-data",
          "key": "node",
          "fields": [
            "currentNode"
          ],
          "values": [
            "namespace"
          ],
          "as": [
            "namespace-node"
          ],
          "default": ""
        },
        {
          "type": "formula",
          "as": "namespace",
          "expr": "datum['namespace-node'] + datum['namespace-pod'] + datum['namespace-container']"
        },
        {
          "type": "stratify",
          "key": "currentNode",
          "parentKey": "parent"
        },
        {
          "type": "pack",
          "field": "value",
          "sort": {
            "field": "value"
          },
          "size": [
            {
              "signal": "width"
            },
            {
              "signal": "height"
            }
          ],
          "padding": 100
        }
      ]
    },
    {
      "name": "focus0",
      "on": [{"trigger": "focus", "insert": "focus"}],
      "transform": [
        {"type": "formula", "expr": "now()", "as": "now"},
        {
          "type": "window",
          "ops": ["row_number"],
          "as": ["row"],
          "sort": {"field": "now", "order": "descending"}
        },
        {"type": "filter", "expr": "datum['row'] ? datum['row'] == 2 : true "},
        {"type": "project", "fields": ["id", "x", "y", "r", "children"]},
        {"type": "formula", "expr": "width/(datum['r']*2)", "as": "k"}
      ]
    },
    {
      "name": "details_data",
      "source": "tree",
      "transform": [
        {
          "type": "filter",
          "expr": "datum['currentNode'] === focus['currentNode'] && showDetails"
        },
        {
          "type": "formula",
          "expr": "[ 'namespace: ' + datum['namespace'], 'type: ' + datum['type'], 'container: ' + datum['container'], 'pod: ' + datum['parent'], 'node: ' + datum['currentNode']]",
          "as": "details"
        }
      ]
    }

  ],
  "legends": [
    {
      "fill": "color",
      "orient": "top-left",
      "encode": {
        "title": {
          "update": {
            "fontSize": {
              "value": 14
            }
          }
        },
        "labels": {
          "interactive": true,
          "update": {
            "fontSize": {
              "value": 12
            },
            "fill": {
              "value": "black"
            }
          },
          "hover": {
            "fill": {
              "value": "firebrick"
            }
          }
        },
        "symbols": {
          "update": {
            "stroke": {
              "value": "transparent"
            }
          }
        },
        "legend": {
          "update": {}
        }
      }
    }
  ],
  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {
        "data": "tree",
        "field": "type"
      },
      "range": {
        "scheme": "magma"
      }
    }
  ],
  "marks": [
    {
      "name": "background",
      "description": "An ivisible rect that covers the entire canvas and sits behind everything",
      "type": "rect",
      "encode": {
        "enter": {
          "x": {"signal": "-padding['left']"},
          "y": {"signal": "-padding['top']"},
          "width": {"signal": "width+padding['left']+padding['right']"},
          "height": {"signal": "height+padding['top']+padding['bottom']"},
          "fillOpacity": {"value": 0},
          "tooltip": [
            {
              "signal": "{'Type': datum.type,'Name': (datum.type == 'container' ? datum.container : datum.currentNode),'Namespace':datum.namespace}"
            }
          ]
        }
      }
    },
    {
      "name": "circles",
      "description": "the zoomable packed circles",
      "type": "symbol",
      "from": {"data": "tree"},
      "encode": {
        "enter": {
          "shape": {"value": "circle"},
          "fill": {"scale": "color", "field": "type"},
          "cursor": {"value": "pointer"},
          "tooltip": [
            {
              "signal": "{'Type': datum.type,'Name': (datum.type == 'container' ? datum.container : datum.currentNode),'Namespace':datum.namespace}"
            }
          ]

        },
        "update": {
          "x": {
            "signal": "lerp([root['x']+ (datum['x'] - focus0['x']) * focus0['k'], root['x'] + (datum['x'] - focus['x']) * k], tEase)"
          },
          "y": {
            "signal": "lerp([ root['y'] + (datum['y'] - focus0['y']) * focus0['k'],  root['y'] + (datum['y'] - focus['y']) * k], tEase)"
          },
          "size": {
            "signal": "pow(2*(datum['r'] * lerp([focus0['k'], k],tEase)),2)"
          },
          "fill": {
            "signal": "showDetails && focus['currentNode'] === datum['currentNode'] ? '#fff' : scale('color',datum['type'])"
          },
          "zindex": {
            "signal": "!showDetails ? 1 : (focus['currentNode'] === root['currentNode'] && isValid(datum['parent'])) ? -99 : indexof(pluck(treeAncestors('tree', datum['currentNode']), 'currentNode'), focus['currentNode']) > 0 ? -99 : 1"
          },
          "stroke": {
            "signal": "showDetails ? scale('color', datum['type']) : luminance(scale('color', datum['type'])) > 0.5 ?  'black' : 'white'"
          },
          "strokeWidth": {
            "signal": "focus['currentNode'] === datum['currentNode'] && showDetails ? 20 : 0.5"
          },
          "strokeOpacity": {
            "signal": "!showDetails ? 0.5 : focus['currentNode'] === root['currentNode'] ? min(tEase, 0.35) : min(tEaseDelayed, 0.35)"
          }
        },
        "hover": {
          "color": {
            "signal": "showDetails ? scale('color', datum['type']) : luminance(scale('color', datum['type'])) > 0.5 ?  'black' : 'white'"
          },
          "strokeWidth": {"value": 2}
        }
      }
    },
    {
      "name": "details_title",
      "details": "the name of the node (appears on zoom)",
      "type": "text",
      "from": {"data": "details_data"},
      "interactive": false,
      "encode": {
        "enter": {
          "text": {"value": "Details"},
          "fill": {"scale": "color", "field": "type"},
          "fontSize": {"signal": "0.055*width"},
          "align": {"value": "center"},
          "x": {"signal": "width/2"},
          "y": {"signal": "height/4"},
          "opacity": {"value": 0}
        },
        "update": {
          "opacity": {
            "signal": "!showDetails ? 0 : focus['currentNode'] === root['currentNode'] ? tEase : tEaseDelayed"
          }
        }
      }
    },
    {
      "name": "details",
      "description": "additional information about the node (appears on zoom)",
      "type": "text",
      "from": {"data": "details_data"},
      "interactive": false,
      "encode": {
        "enter": {
          "text": {"signal": "datum['details']"},
          "fontSize": {"signal": "0.045*width"},
          "align": {"value": "left"},
          "x": {"signal": "width/6"},
          "y": {"signal": "height/3"},
          "fill": {"value": "gray"},
          "opacity": {"value": 0}
        },
        "update": {
          "opacity": {
            "signal": "!showDetails ? 0 : focus['currentNode'] === root['currentNode'] ? tEase : tEaseDelayed"
          }
        }
      }
    },
    {
      "name": "helper_text",
      "interactive": false,
      "description": "interactivity instructions located at the bottom of left",
      "type": "text",
      "encode": {
        "enter": {
          "fontSize": {"value": 14},
          "text": {
            "signal": "['interactivity instructions:', '• click on a node to zoom-in','• for nodes with children, shift + click to see details for that node', '• to slow down animations, ⌘ + click (Mac) / ⊞ + click (Windows)']"
          },
          "y": {"signal": "height+5"}
        },
        "update": {
          "opacity": {
            "signal": "ceil(k) === 1 ? isValid(t) ? tEaseDelayed : 1 : 0"
          }
        }
      }
    }

  ]
}
"}}
